// imports will always go at the top
import chatMsg from './components/ChatMessage.js';
import login from './components/login.js';

const socket = io();

// utility functions for socket
function setUserID({ sID }) {
    // save our unique ID generated by Socket on the srver side - this is how we track individual connections to the chat service
    vm.socketID = sID;
}

function showNewMessage({ message }) {
    vm.messages.push(message);
}

function handleUserTyping(user) {
    console.log('user is typing');
}

//adding audio functionality
function playAudio() {
    var audio = new Audio('./audio/chat.mp3');

    if (vm.messages) {
      audio.play();
    }
    else {
      audio.pause();
    };

  };


const { createApp } = Vue

const vm = createApp({
    data() {
        return {
            socketID: '',
            message: '',
            messages: [],
            login: [],
            nickname: '',
            msg: '',
            joined: false,
            active_user: '',
            
            

        }
    },

    methods: {
        dispatchMessage() {
            socket.emit('chat_message',
                {
                    content: this.message,
                    name: this.nickname || "anonymous",
                    id: this.socketID
                })
            this.message = "";
        },

        catchTextFocus() {
            //emit a custom typing event and broadcast it to the server
            socket.emit('user_typing', {
                name: this.nickname || 'anonymous'
            })
        }
    },

    components: {
        newmsg: chatMsg,
        login: login
    }



}).mount('#app')

socket.addEventListener('connected', setUserID);
socket.addEventListener('new_message', showNewMessage);
socket.addEventListener('typing', handleUserTyping);
